const express = require('express');
const cors = require('cors');
const morgan = require('morgan');
// const path = require('path'); // Commented out unused import
const routes = require('./routes/index.js'); // Added file extension
const gitRoutes = require('./routes/git');

const app = express();
const PORT = process.env.PORT || 3001;

// CORS configuration
const corsOptions = {
  origin: ['http://localhost:3000', 'http://127.0.0.1:3000'],
  methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
  allowedHeaders: ['Content-Type', 'Authorization'],
  credentials: true,
  optionsSuccessStatus: 200,
};

// Middleware
app.use(cors(corsOptions));

// Increase payload limit to 50mb
app.use(express.json({
  limit: '50mb',
  type: ['application/json', 'text/plain'],
}));
app.use(express.urlencoded({
  limit: '50mb',
  extended: true,
}));
app.use(morgan('dev'));

// Error handling for JSON parsing
app.use((err, req, res, next) => {
  if (err instanceof SyntaxError && err.status === 400 && 'body' in err) {
    return res.status(400).json({
      error: 'Invalid JSON',
      details: err.message,
    });
  }
  return next();
});

// API Routes
app.use('/api', routes);
// The git routes are already included via routes/index.js
// app.use('/api/git', gitRoutes); // Removing this to avoid conflicts

// Add a direct route for the old /api/analyze endpoint
app.post('/api/analyze', (req, res) => {
  // Forward the request to our code review analyze endpoint
  req.url = '/code-review/analyze';
  routes(req, res);
});

// Debug available routes
app._router.stack.forEach(function (middleware) {
  if (middleware.route) { // routes registered directly on the app
    console.log(`Route: ${JSON.stringify(middleware.route.path)}`);
  } else if (middleware.name === 'router') { // router middleware 
    middleware.handle.stack.forEach(function (handler) {
      const route = handler.route;
      if (route) {
        const methods = Object.keys(route.methods).join(', ').toUpperCase();
        console.log(`Route: ${methods} ${route.path}`);
      }
    });
  }
});

// Handle 404
app.use((req, res) => {
  res.status(404).json({ error: 'Not Found' });
});

// Global error handler
app.use((err, req, res, _next) => {
  console.error('Global error:', err);
  return res.status(500).json({
    error: 'Internal Server Error',
    details: process.env.NODE_ENV === 'development' ? err.message : undefined,
  });
});

// Function to try different ports
const startServer = (port) => {
  try {
    const server = app.listen(port, () => {
      console.info(`Server running on port ${port}`);
    });

    server.on('error', (err) => {
      if (err.code === 'EADDRINUSE') {
        console.info(`Port ${port} is busy, trying ${port + 1}...`);
        startServer(port + 1);
      } else {
        console.error('Server error:', err);
      }
    });
  } catch (err) {
    console.error('Failed to start server:', err);
  }
};

// Start the server
startServer(PORT);

const express = require('express');
const CodeReviewService = require('../src/services/CodeReviewService');
const SecureGitService = require('../src/services/SecureGitService');
const logger = require('../src/utils/logger');

const router = express.Router();
const codeReviewService = new CodeReviewService();
const secureGitService = new SecureGitService();

/**
 * Get all repositories
 * Endpoint for listing available repositories
 */
router.get('/repositories', async (req, res) => {
  try {
    const repositories = await SecureGitService.findRepositories();
    res.json(repositories);
  } catch (error) {
    logger.error('Error fetching repositories:', error);
    res.status(500).json({ error: error.message });
  }
});

/**
 * Select repository
 * Endpoint for setting the current repository
 */
router.post('/select-repository', async (req, res) => {
  try {
    const { path } = req.body;

    if (!path) {
      return res.status(400).json({ error: 'Repository path is required' });
    }

    const isPathSet = secureGitService.setRepositoryPath(path);

    if (!isPathSet) {
      return res.status(403).json({ error: 'Access to this repository path is not allowed' });
    }

    const isValid = await secureGitService.isValidRepo(path);

    if (!isValid) {
      return res.status(400).json({ error: 'Invalid Git repository' });
    }

    res.json({ success: true, path });
  } catch (error) {
    logger.error('Error selecting repository:', error);
    res.status(500).json({ error: error.message });
  }
});

/**
 * Get branches
 * Endpoint for listing branches in the selected repository
 */
router.get('/branches', async (req, res) => {
  try {
    const repoPath = secureGitService.getRepositoryPath();

    if (!repoPath) {
      return res.status(400).json({ error: 'No repository selected' });
    }

    const branches = await secureGitService.getBranches();
    res.json(branches);
  } catch (error) {
    logger.error('Error fetching branches:', error);
    res.status(500).json({ error: error.message });
  }
});

/**
 * Review pull request
 * Endpoint for analyzing code differences between branches
 */
router.post('/analyze', async (req, res) => {
  try {
    const { baseBranch, headBranch, fromBranch, toBranch } = req.body;
    const repoPath = secureGitService.getRepositoryPath() || req.body.repoPath;

    // Support both naming conventions (from old and new components)
    const actualBaseBranch = baseBranch || fromBranch;
    const actualHeadBranch = headBranch || toBranch;

    if (!repoPath) {
      return res.status(400).json({ error: 'No repository selected' });
    }

    if (!actualBaseBranch || !actualHeadBranch) {
      return res.status(400).json({ error: 'Base and head branches are required' });
    }

    // Set the repository path if it's provided directly
    if (req.body.repoPath && !secureGitService.getRepositoryPath()) {
      const isPathSet = secureGitService.setRepositoryPath(req.body.repoPath);
      if (!isPathSet) {
        return res.status(403).json({ error: 'Access to this repository path is not allowed' });
      }
    }

    // Log the analysis request
    logger.info(`Starting code review for ${actualHeadBranch} against ${actualBaseBranch}`);

    // Set a longer timeout for complex reviews
    req.setTimeout(300000); // 5 minutes

    // Perform the code review with token optimization
    const reviewResults = await codeReviewService.reviewPullRequest(repoPath, actualBaseBranch, actualHeadBranch);

    res.json(reviewResults);
  } catch (error) {
    logger.error('Error analyzing code:', error);
    res.status(500).json({ error: error.message });
  }
});

/**
 * Get diff between branches
 * Endpoint for getting the raw diff between two branches
 */
router.get('/diff', async (req, res) => {
  try {
    const { baseBranch, headBranch } = req.query;
    const repoPath = secureGitService.getRepositoryPath();

    if (!repoPath) {
      return res.status(400).json({ error: 'No repository selected' });
    }

    if (!baseBranch || !headBranch) {
      return res.status(400).json({ error: 'Base and head branches are required' });
    }

    const diffOutput = await codeReviewService.getDiffBetweenBranches(repoPath, baseBranch, headBranch);
    const files = await codeReviewService.parseGitDiff(diffOutput);

    res.json({ files });
  } catch (error) {
    logger.error('Error getting diff:', error);
    res.status(500).json({ error: error.message });
  }
});

/**
 * Get review status
 * Endpoint for checking the status of ongoing analyses
 */
router.get('/status', (req, res) => {
  // This could be extended to support progress tracking for long-running analyses
  res.json({ status: 'ready' });
});

module.exports = router;

const express = require('express');
const { exec } = require('child_process');
const router = express.Router();

// Simple health check endpoint
router.get('/health', (req, res) => {
  res.json({ status: 'ok' });
});

router.post('/branches', async (req, res) => {
  try {
    console.log('Received request for branches with body:', req.body);
    const { path } = req.body;

    if (!path) {
      console.log('No path provided');
      return res.status(400).json({ error: 'Repository path is required' });
    }

    console.log(`Executing git command in directory: ${path}`);

    // Execute git command to list branches
    exec('git branch', { cwd: path }, (error, stdout, stderr) => {
      if (error) {
        console.error(`Error executing git command: ${error}`);
        return res.status(500).json({
          error: 'Failed to fetch branches',
          details: error.message
        });
      }

      if (stderr) {
        console.error(`Git command stderr: ${stderr}`);
      }

      // Parse branches from git output
      const branches = stdout
        .split('\n')
        .map(branch => branch.trim())
        .filter(branch => branch)
        .map(branch => branch.replace('* ', '')); // Remove the asterisk from current branch

      console.log(`Found branches: ${JSON.stringify(branches)}`);
      res.json({ branches });
    });
  } catch (error) {
    console.error('Error in /branches endpoint:', error);
    res.status(500).json({
      error: 'Internal server error',
      details: error.message
    });
  }
});

module.exports = router;

