const path = require('path');
const fs = require('fs').promises;
const { execSync } = require('child_process');
const logger = require('../utils/logger');
const axios = require('axios');

class CodeReviewService {
  constructor() {
    this.apiKey = process.env.GEMINI_API_KEY;
    this.baseUrl = 'https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent';
    this.maxRetries = 3;
    this.maxTokensPerRequest = 4096; // Control token usage 
  }

  /**
   * Review code changes in a pull request
   * @param {string} repoPath - Path to the repository
   * @param {string} baseBranch - Base branch (e.g., main)
   * @param {string} headBranch - Head branch (feature branch)
   * @returns {Promise<Object>} - Review results
   */
  async reviewPullRequest(repoPath, baseBranch, headBranch) {
    try {
      // Get the diff between branches
      const diffOutput = await this.getDiffBetweenBranches(repoPath, baseBranch, headBranch);

      // Parse the diff into files
      const files = await this.parseGitDiff(diffOutput);

      // Analyze files in chunks to optimize token usage
      const reviewResults = await this.analyzeFilesWithTokenOptimization(files);

      return {
        summary: this.generateSummary(reviewResults),
        files: reviewResults
      };
    } catch (error) {
      logger.error('Error reviewing pull request:', error);
      throw new Error(`Failed to review pull request: ${error.message}`);
    }
  }

  /**
   * Get diff between two branches
   * @param {string} repoPath - Repository path
   * @param {string} baseBranch - Base branch
   * @param {string} headBranch - Head branch
   * @returns {Promise<string>} - Git diff output
   */
  async getDiffBetweenBranches(repoPath, baseBranch, headBranch) {
    try {
      const diffCommand = `git diff ${baseBranch}...${headBranch}`;
      const diffOutput = execSync(diffCommand, { cwd: repoPath, maxBuffer: 10 * 1024 * 1024 }).toString();
      return diffOutput;
    } catch (error) {
      logger.error('Error getting diff between branches:', error);
      throw new Error(`Failed to get diff between branches: ${error.message}`);
    }
  }

  /**
   * Parse git diff output into structured file objects
   * @param {string} diffOutput - Git diff output
   * @returns {Promise<Array>} - Array of file objects
   */
  async parseGitDiff(diffOutput) {
    const files = [];
    const diffFiles = diffOutput.split('diff --git ').filter(Boolean);

    for (const fileDiff of diffFiles) {
      try {
        const filePathMatch = fileDiff.match(/a\/(.*) b\/(.*)/);
        if (!filePathMatch) continue;

        const filePath = filePathMatch[2];
        const extension = path.extname(filePath).substring(1);

        // Extract changed lines
        const hunkMatches = [...fileDiff.matchAll(/@@\s-\d+,\d+\s\+\d+,\d+\s@@/g)];
        const hunks = [];

        if (hunkMatches.length > 0) {
          for (let i = 0; i < hunkMatches.length; i++) {
            const currentMatchIndex = hunkMatches[i].index;
            const nextMatchIndex = i < hunkMatches.length - 1 ? hunkMatches[i + 1].index : fileDiff.length;
            const hunkContent = fileDiff.substring(currentMatchIndex, nextMatchIndex);
            hunks.push(hunkContent);
          }
        }

        files.push({
          path: filePath,
          extension,
          hunks,
          content: fileDiff,
          type: this.getFileType(extension)
        });
      } catch (error) {
        logger.warn(`Error parsing diff for a file: ${error.message}`);
      }
    }

    return files;
  }

  /**
   * Get file type based on extension
   * @param {string} extension - File extension
   * @returns {string} - File type
   */
  getFileType(extension) {
    const fileTypes = {
      js: 'JavaScript',
      jsx: 'React',
      ts: 'TypeScript',
      tsx: 'React TypeScript',
      py: 'Python',
      java: 'Java',
      rb: 'Ruby',
      go: 'Go',
      rs: 'Rust',
      php: 'PHP',
      cs: 'C#',
      cpp: 'C++',
      c: 'C',
      swift: 'Swift',
      kt: 'Kotlin',
      md: 'Markdown',
      json: 'JSON',
      yml: 'YAML',
      yaml: 'YAML',
      css: 'CSS',
      scss: 'SCSS',
      html: 'HTML',
      xml: 'XML'
    };

    return fileTypes[extension] || 'Unknown';
  }

  /**
   * Analyze files with token optimization
   * @param {Array} files - Array of file objects
   * @returns {Promise<Array>} - Analysis results for each file
   */
  async analyzeFilesWithTokenOptimization(files) {
    // Group files by type to enable domain-specific analysis
    const filesByType = this.groupFilesByType(files);
    const results = [];

    // Process each file type group
    for (const [type, typeFiles] of Object.entries(filesByType)) {
      // Further chunk the files within each type to optimize token usage
      const fileChunks = this.chunkFilesByTokenSize(typeFiles);

      for (const chunk of fileChunks) {
        try {
          // Get language-specific review for this chunk
          const chunkReviews = await this.getAIReviewForChunk(chunk, type);
          results.push(...chunkReviews);
        } catch (error) {
          logger.error(`Error reviewing ${type} files chunk:`, error);
          // Add basic info for files that failed analysis
          chunk.forEach(file => {
            results.push({
              path: file.path,
              type: file.type,
              issues: [{ title: 'Analysis failed', severity: 'low' }],
              suggestions: ['File could not be analyzed due to an error.']
            });
          });
        }
      }
    }

    return results;
  }

  /**
   * Group files by their type for more focused analysis
   * @param {Array} files - Array of file objects
   * @returns {Object} - Files grouped by type
   */
  groupFilesByType(files) {
    const filesByType = {};

    for (const file of files) {
      if (!filesByType[file.type]) {
        filesByType[file.type] = [];
      }
      filesByType[file.type].push(file);
    }

    return filesByType;
  }

  /**
   * Chunk files based on estimated token size to optimize API calls
   * @param {Array} files - Array of file objects
   * @returns {Array} - Array of file chunks
   */
  chunkFilesByTokenSize(files) {
    const chunks = [];
    let currentChunk = [];
    let currentTokens = 0;

    for (const file of files) {
      // Rough token estimation based on content length
      const estimatedTokens = Math.ceil(file.content.length / 4);

      if (currentTokens + estimatedTokens > this.maxTokensPerRequest && currentChunk.length > 0) {
        chunks.push([...currentChunk]);
        currentChunk = [];
        currentTokens = 0;
      }

      currentChunk.push(file);
      currentTokens += estimatedTokens;
    }

    if (currentChunk.length > 0) {
      chunks.push(currentChunk);
    }

    return chunks;
  }

  /**
   * Get AI review for a chunk of files
   * @param {Array} files - Array of file objects
   * @param {string} fileType - Type of files being reviewed
   * @returns {Promise<Array>} - Review results
   */
  async getAIReviewForChunk(files, fileType) {
    // Create optimized prompt that focuses analysis on the specific file type
    const prompt = this.createTypeSpecificPrompt(files, fileType);

    // Call Gemini API
    const response = await this.callGeminiAPI(prompt);

    // Parse and validate the response
    return this.parseReviewResponse(response, files);
  }

  /**
   * Create type-specific prompt for better analysis
   * @param {Array} files - Array of file objects
   * @param {string} fileType - Type of files being reviewed
   * @returns {string} - Enhanced prompt
   */
  createTypeSpecificPrompt(files, fileType) {
    // Language-specific guidelines based on file type
    const languageGuidelines = {
      'JavaScript': 'Focus on modern JS practices, ES6+ features, potential memory leaks, and performance issues.',
      'TypeScript': 'Check for proper type usage, interface definitions, and type safety issues.',
      'Python': 'Focus on PEP 8 compliance, Pythonic patterns, and common anti-patterns.',
      'Java': 'Check for OOP best practices, thread safety issues, and performance considerations.',
      // Add more language-specific guidelines as needed
    };

    const guidelines = languageGuidelines[fileType] || 'Focus on code quality, maintainability, and potential bugs.';

    // Create compact diff representation with only essential information
    const filesDiffSummary = files.map(file => {
      return {
        path: file.path,
        hunks: file.hunks.length > 0 ? file.hunks : ['File changed'],
      };
    });

    return `
You are a senior ${fileType} developer performing a code review. Review the following file changes.

${guidelines}

For each file, identify:
1. Potential bugs or errors
2. Performance issues
3. Security vulnerabilities
4. Code style and maintainability concerns
5. Suggested improvements

Format your response as JSON with the following structure for each file:
{
  "files": [
    {
      "path": "file path",
      "issues": [
        {
          "title": "Brief issue title",
          "description": "Detailed description",
          "severity": "high|medium|low",
          "line": "line number or range (if applicable)"
        }
      ],
      "suggestions": [
        "Specific actionable suggestion"
      ]
    }
  ]
}

Here are the code changes to review:
${JSON.stringify(filesDiffSummary, null, 2)}
`;
  }

  /**
   * Call the Gemini API
   * @param {string} prompt - Enhanced prompt
   * @returns {Promise<Object>} - Raw API response
   */
  async callGeminiAPI(prompt) {
    const url = `${this.baseUrl}?key=${this.apiKey}`;

    const requestBody = {
      contents: [
        {
          parts: [
            {
              text: prompt
            }
          ]
        }
      ],
      generationConfig: {
        temperature: 0.2,
        topK: 40,
        topP: 0.95,
        maxOutputTokens: 4096, // Limit output tokens
      }
    };

    try {
      const response = await axios.post(url, requestBody, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      return response.data;
    } catch (error) {
      logger.error('Error calling Gemini API:', error.response?.data || error.message);
      throw new Error(`Gemini API error: ${error.message}`);
    }
  }

  /**
   * Parse the review response from Gemini API
   * @param {Object} response - Raw API response
   * @param {Array} originalFiles - Original files that were analyzed
   * @returns {Array} - Parsed file reviews
   */
  parseReviewResponse(response, originalFiles) {
    try {
      // Extract text content from Gemini response
      const textContent = response.candidates[0].content.parts[0].text;

      // Find JSON in the response
      const jsonMatch = textContent.match(/\{[\s\S]*\}/);

      if (!jsonMatch) {
        throw new Error('No JSON found in Gemini response');
      }

      // Parse the JSON
      const jsonResponse = JSON.parse(jsonMatch[0]);

      // Ensure we have file reviews
      if (!jsonResponse.files || !Array.isArray(jsonResponse.files)) {
        throw new Error('Invalid response format: missing files array');
      }

      // Match response to original files to ensure all files have reviews
      return this.matchReviewsToOriginalFiles(jsonResponse.files, originalFiles);
    } catch (error) {
      logger.error('Error parsing Gemini response:', error);
      // Return basic information for the files
      return originalFiles.map(file => ({
        path: file.path,
        type: file.type,
        issues: [],
        suggestions: ['Failed to parse AI analysis.']
      }));
    }
  }

  /**
   * Match reviews to original files
   * @param {Array} fileReviews - Reviews returned by Gemini
   * @param {Array} originalFiles - Original files that were analyzed
   * @returns {Array} - Complete file reviews
   */
  matchReviewsToOriginalFiles(fileReviews, originalFiles) {
    const reviewsByPath = {};

    // Create lookup by path
    fileReviews.forEach(review => {
      if (review.path) {
        reviewsByPath[review.path] = review;
      }
    });

    // Ensure all original files have reviews
    return originalFiles.map(file => {
      const review = reviewsByPath[file.path];

      if (review) {
        return {
          path: file.path,
          type: file.type,
          issues: review.issues || [],
          suggestions: review.suggestions || []
        };
      }

      // Default for files without reviews
      return {
        path: file.path,
        type: file.type,
        issues: [],
        suggestions: ['No specific issues found.']
      };
    });
  }

  /**
   * Generate a summary of all file reviews
   * @param {Array} fileReviews - Reviews for all files
   * @returns {Object} - Summary object
   */
  generateSummary(fileReviews) {
    const issuesBySeverity = {
      high: 0,
      medium: 0,
      low: 0
    };

    // Count issues by severity
    fileReviews.forEach(file => {
      if (file.issues && Array.isArray(file.issues)) {
        file.issues.forEach(issue => {
          if (issue.severity && issuesBySeverity[issue.severity] !== undefined) {
            issuesBySeverity[issue.severity]++;
          }
        });
      }
    });

    // Determine overall quality
    let overallQuality = 'good';
    if (issuesBySeverity.high > 0) {
      overallQuality = 'needs work';
    } else if (issuesBySeverity.medium > 3) {
      overallQuality = 'fair';
    }

    // Extract top issues
    const allIssues = fileReviews.flatMap(file =>
      (file.issues || []).map(issue => ({
        ...issue,
        file: file.path
      }))
    );

    const highPriorityIssues = allIssues
      .filter(issue => issue.severity === 'high')
      .slice(0, 3);

    return {
      filesAnalyzed: fileReviews.length,
      issueCount: {
        total: Object.values(issuesBySeverity).reduce((a, b) => a + b, 0),
        ...issuesBySeverity
      },
      overallQuality,
      topIssues: highPriorityIssues,
      generatedAt: new Date().toISOString()
    };
  }
}

module.exports = CodeReviewService;

const simpleGit = require('simple-git');
const path = require('path');
const fs = require('fs').promises;
const fsSync = require('fs');
const os = require('os');
const logger = require('../utils/logger');
const config = require('../config');

// Helper function to validate paths before fs operations
const validatePath = (filePath, allowedPaths = []) => {
  if (!filePath) return false;

  const normalizedPath = path.normalize(filePath);

  // Check for path traversal attempts
  if (normalizedPath.includes('..')) return false;

  // If allowedPaths is provided, check against the whitelist
  if (allowedPaths.length > 0) {
    return allowedPaths.some((allowedPath) => {
      const normalizedAllowedPath = path.normalize(allowedPath);
      return normalizedPath.startsWith(normalizedAllowedPath);
    });
  }

  // Default validation - check if path is absolute and within safe directories
  const safeDirectories = [
    path.resolve(__dirname, '..', '..'), // Project root
    os.homedir(),
  ];

  return safeDirectories.some((safeDir) => normalizedPath.startsWith(safeDir));
};

class GitService {
  constructor(repoPath = '') {
    this.repoPath = repoPath;
    this.git = simpleGit(repoPath);
    // Ensure the state directory exists
    const stateDir = path.join(__dirname, '..', '..', path.dirname(config.git.statePath));
    if (!fsSync.existsSync(stateDir)) {
      fsSync.mkdirSync(stateDir, { recursive: true });
    }
    this.stateFilePath = path.join(__dirname, '..', '..', config.git.statePath);
  }

  /**
   * Set the repository path
   * @param {string} repoPath - Path to the repository
   */
  setRepoPath(repoPath) {
    this.repoPath = repoPath;
    this.git = simpleGit(repoPath);
  }

  /**
   * Check if the current path is a valid Git repository
   * @returns {Promise<boolean>} - True if valid Git repository
   */
  async isValidRepo() {
    try {
      return await this.git.checkIsRepo();
    } catch (error) {
      logger.error('Error checking if valid repo:', error);
      return false;
    }
  }

  /**
   * Get all branches in the repository
   * @returns {Promise<Object>} - Object containing branch information
   */
  async getBranches() {
    try {
      return await this.git.branch();
    } catch (error) {
      logger.error('Error getting branches:', error);
      throw error;
    }
  }

  /**
   * Get the current branch
   * @returns {Promise<string>} - Current branch name
   */
  async getCurrentBranch() {
    try {
      const { current } = await this.git.branch();
      return current;
    } catch (error) {
      logger.error('Error getting current branch:', error);
      throw error;
    }
  }

  /**
   * Get diff between two branches
   * @param {string} fromBranch - Source branch
   * @param {string} toBranch - Target branch
   * @returns {Promise<string>} - Diff output
   */
  async getDiff(fromBranch, toBranch) {
    try {
      return await this.git.diff([fromBranch, toBranch]);
    } catch (error) {
      logger.error('Error getting diff:', error);
      throw error;
    }
  }

  /**
   * Load repository path from state file
   * @returns {Promise<string>} - Repository path
   */
  async loadState() {
    try {
      logger.info('Loading state from:', this.stateFilePath);

      // Validate path before file operations
      if (!validatePath(this.stateFilePath)) {
        logger.error('Invalid state file path');
        return '';
      }

      // Check if file exists
      if (!fsSync.existsSync(this.stateFilePath)) {
        logger.info('State file does not exist, creating empty state');
        await this.saveState();
        return '';
      }

      // Read and parse state file
      const stateContent = await fs.readFile(this.stateFilePath, 'utf8');
      const state = JSON.parse(stateContent);
      if (state && state.repoPath) {
        this.setRepoPath(state.repoPath);
        return state.repoPath;
      }
      return '';
    } catch (error) {
      logger.error('Error loading state:', error);
      return '';
    }
  }

  /**
   * Save repository path to state file
   * @returns {Promise<void>}
   */
  async saveState() {
    try {
      logger.info('Saving state, repoPath:', this.repoPath);

      // Validate path before file operations
      if (!validatePath(this.stateFilePath)) {
        throw new Error('Invalid state file path');
      }

      // eslint-disable-next-line security/detect-non-literal-fs-filename
      await fs.writeFile(this.stateFilePath, JSON.stringify({ repoPath: this.repoPath }));
      logger.info('State saved successfully');
    } catch (error) {
      logger.error('Error saving state:', error);
      throw error;
    }
  }

  /**
   * Find Git repositories in common directories
   * @returns {Promise<Array>} - Array of repository objects
   */
  static async findRepositories() {
    try {
      const homeDir = os.homedir();
      // Common places to find repositories
      const commonDirs = [
        path.join(homeDir, 'Documents'),
        path.join(homeDir, 'Projects'),
        path.join(homeDir, 'Development'),
        path.join(homeDir, 'Code'),
        path.join(homeDir, 'Github'),
      ];

      // Add the current directory to the list for testing
      const currentDir = process.cwd();
      commonDirs.push(path.dirname(currentDir));

      logger.info('Searching for repositories in directories:', commonDirs);

      const repositories = [];
      const validDirs = [];

      // First validate all directories before attempting to search them
      for (const dir of commonDirs) {
        if (validatePath(dir)) {
          try {
            // eslint-disable-next-line security/detect-non-literal-fs-filename
            const dirStat = await fs.stat(dir).catch(() => null);
            if (dirStat && dirStat.isDirectory()) {
              validDirs.push(dir);
            }
          } catch (error) {
            logger.warn(`Error checking directory ${dir}:`, error.message);
          }
        }
      }

      logger.info('Valid directories to search:', validDirs);

      // Process each directory one by one (safer than parallel)
      for (const dir of validDirs) {
        try {
          // Get subdirectories
          // eslint-disable-next-line security/detect-non-literal-fs-filename
          const items = await fs.readdir(dir, { withFileTypes: true }).catch((err) => {
            logger.warn(`Error reading directory ${dir}:`, err.message);
            return [];
          });

          if (!items || !items.length) continue;

          const subdirs = items
            .filter((item) => item.isDirectory())
            .map((item) => path.join(dir, item.name));

          // Check each subdirectory for .git folder (with a reasonable limit)
          const checkLimit = Math.min(subdirs.length, 25); // limit to 25 directories per parent
          logger.info(`Checking ${checkLimit} subdirectories in ${dir}`);

          for (let i = 0; i < checkLimit; i++) {
            const subdir = subdirs[i];
            try {
              // Skip if path validation fails
              if (!validatePath(subdir)) continue;

              const gitDir = path.join(subdir, '.git');
              if (!validatePath(gitDir)) continue;

              // Check if .git directory exists
              // eslint-disable-next-line security/detect-non-literal-fs-filename
              const gitDirStat = await fs.stat(gitDir).catch(() => null);
              if (!gitDirStat || !gitDirStat.isDirectory()) continue;

              // It's a git repository - add it without doing any git operations that might hang
              repositories.push({
                path: subdir,
                name: path.basename(subdir),
              });
            } catch (error) {
              logger.warn(`Error checking repository ${subdir}:`, error.message);
            }
          }
        } catch (error) {
          logger.warn(`Error processing directory ${dir}:`, error.message);
        }
      }

      logger.info(`Found ${repositories.length} repositories`);
      return repositories;
    } catch (error) {
      logger.error('Error finding repositories:', error);
      return [];
    }
  }

  /**
   * Check if the given path is a valid Git repository
   * @param {string} dirPath - Path to the directory
   * @returns {Promise<boolean>} - True if valid Git repository
   */
  async isGitRepository(dirPath) {
    try {
      const git = simpleGit(dirPath);
      return await git.checkIsRepo();
    } catch (error) {
      return false;
    }
  }

  /**
   * Get the content of a file at a specific commit or branch
   * @param {string} filePath - Path to the file
   * @param {string} ref - Commit hash or branch name
   * @returns {Promise<string>} - File content
   */
  async getFileContent(filePath, ref) {
    try {
      return await this.git.show([`${ref}:${filePath}`]);
    } catch (error) {
      logger.error(`Error getting file content for ${filePath} at ${ref}:`, error);
      throw error;
    }
  }

  /**
   * Get the commit history of a branch
   * @param {string} branch - Branch name
   * @param {number} [maxCount=100] - Maximum number of commits to retrieve
   * @returns {Promise<Object>} - Commit history
   */
  async getCommitHistory(branch, maxCount = 100) {
    try {
      return await this.git.log({
        maxCount,
        branch,
      });
    } catch (error) {
      logger.error('Error getting commit history:', error);
      throw error;
    }
  }

  /**
   * Get the status of the repository
   * @returns {Promise<Object>} - Repository status
   */
  async getStatus() {
    try {
      return await this.git.status();
    } catch (error) {
      logger.error('Error getting git status:', error);
      throw error;
    }
  }
}

module.exports = GitService;

const path = require('path');
const os = require('os');
const fs = require('fs').promises;
const { exec } = require('child_process');
const logger = require('../utils/logger');

class SecureGitService {
  constructor() {
    this.currentRepoPath = null;
    this.allowedDirectories = [
      'Documents', 'Projects', 'Development', 'Code',
      'Github', 'repos', 'git', 'workspace', 'dev', 'Desktop',
    ].map((dir) => path.join(os.homedir(), dir));
  }

  setRepositoryPath(repoPath) {
    if (!repoPath) return false;

    const expandedPath = repoPath.startsWith('~')
      ? path.join(os.homedir(), repoPath.slice(1))
      : repoPath;

    const normalizedPath = path.normalize(expandedPath);

    if (this.isPathAllowed(normalizedPath)) {
      this.currentRepoPath = normalizedPath;
      return true;
    }

    logger.warn(`Attempted access to unauthorized path: ${normalizedPath}`);
    return false;
  }

  getRepositoryPath() {
    return this.currentRepoPath;
  }

  isPathAllowed(pathToCheck) {
    return this.allowedDirectories.some((dir) => pathToCheck.startsWith(dir) && !pathToCheck.includes('..'));
  }

  async isValidRepo(repoPath) {
    if (!repoPath) return false;
    try {
      const gitDir = path.join(repoPath, '.git');
      await fs.access(gitDir);
      return true;
    } catch {
      return false;
    }
  }

  async getBranches() {
    if (!this.currentRepoPath) {
      throw new Error('No repository selected');
    }

    return new Promise((resolve, reject) => {
      exec('git branch', { cwd: this.currentRepoPath }, (error, stdout, stderr) => {
        if (error) {
          logger.error(`Error getting branches: ${error.message}`);
          return reject(error);
        }

        const all = stdout
          .split('\n')
          .map(branch => branch.trim())
          .filter(branch => branch)
          .map(branch => branch.replace('* ', '')); // Remove the asterisk from current branch

        const current = stdout
          .split('\n')
          .find(branch => branch.trim().startsWith('* '));

        const currentBranch = current ? current.replace('* ', '').trim() : '';

        resolve({
          all,
          current: currentBranch,
        });
      });
    });
  }

  async getDiff(fromBranch, toBranch) {
    if (!this.currentRepoPath) {
      throw new Error('No repository selected');
    }

    if (!fromBranch || !toBranch) {
      throw new Error('Both branches must be specified');
    }

    return new Promise((resolve, reject) => {
      const cmd = `git diff --name-status ${fromBranch}..${toBranch}`;
      exec(cmd, { cwd: this.currentRepoPath }, (error, stdout, stderr) => {
        if (error) {
          logger.error(`Error getting diff: ${error.message}`);
          return reject(error);
        }

        const changes = stdout
          .split('\n')
          .filter(line => line.trim())
          .map(line => {
            const [status, file] = line.split('\t');
            return { status, file };
          });

        resolve(changes);
      });
    });
  }

  static async findRepositories() {
    const repos = [];
    const searchDirs = [
      'Documents', 'Projects', 'Development', 'Code',
      'Github', 'repos', 'git', 'workspace', 'dev', 'Desktop',
    ].map((dir) => path.join(os.homedir(), dir));

    for (const dir of searchDirs) {
      try {
        await this.searchDirectory(dir, repos);
      } catch (error) {
        logger.error(`Error searching directory ${dir}:`, error);
      }
    }

    return repos;
  }

  static async searchDirectory(dir, repos, depth = 0) {
    if (depth > 3) return;

    try {
      const entries = await fs.readdir(dir, { withFileTypes: true });

      if (entries.some((entry) => entry.name === '.git')) {
        repos.push({
          path: dir,
          name: path.basename(dir),
        });
        return;
      }

      for (const entry of entries) {
        if (entry.isDirectory() && !entry.name.startsWith('.')) {
          await this.searchDirectory(path.join(dir, entry.name), repos, depth + 1);
        }
      }
    } catch (error) {
      logger.debug(`Skipping inaccessible directory ${dir}`);
    }
  }
}

module.exports = SecureGitService;

const fs = require('fs').promises;
const path = require('path');
const logger = require('./logger');

class CoverageTracker {
  constructor() {
    this.coverageData = new Map();
    this.summaryPath = path.join(__dirname, '../../coverage/summary.json');
    this.historyPath = path.join(__dirname, '../../coverage/history.json');
  }

  async trackCoverage(testResults) {
    const timestamp = new Date().toISOString();
    const coverage = {
      timestamp,
      overall: testResults.coverageMap.getCoverageSummary(),
      files: new Map(),
    };

    for (const [filePath, fileCoverage] of Object.entries(testResults.coverageMap.data)) {
      coverage.files.set(filePath, {
        statements: fileCoverage.getLineCoverage(),
        branches: fileCoverage.getBranchCoverage(),
        functions: fileCoverage.getFunctionCoverage(),
      });
    }

    this.coverageData.set(timestamp, coverage);
    await this.saveCoverageData();
  }

  async saveCoverageData() {
    try {
      const summary = this.generateSummary();
      await fs.writeFile(this.summaryPath, JSON.stringify(summary, null, 2));

      const history = Array.from(this.coverageData.entries()).map(([timestamp, data]) => ({
        timestamp,
        coverage: {
          statements: data.overall.statements.pct,
          branches: data.overall.branches.pct,
          functions: data.overall.functions.pct,
        },
      }));

      await fs.writeFile(this.historyPath, JSON.stringify(history, null, 2));
    } catch (error) {
      logger.error('Error saving coverage data:', error);
    }
  }

  generateSummary() {
    const latest = Array.from(this.coverageData.values()).pop();
    if (!latest) return null;

    return {
      timestamp: new Date().toISOString(),
      overall: {
        statements: latest.overall.statements.pct,
        branches: latest.overall.branches.pct,
        functions: latest.overall.functions.pct,
      },
      files: Array.from(latest.files.entries()).map(([file, coverage]) => ({
        file: path.relative(process.cwd(), file),
        coverage,
      })),
    };
  }

  getFileCoverage(filePath) {
    const latest = Array.from(this.coverageData.values()).pop();
    return latest?.files.get(filePath) || null;
  }

  getOverallCoverage() {
    const latest = Array.from(this.coverageData.values()).pop();
    return latest?.overall || null;
  }

  getCoverageHistory() {
    return Array.from(this.coverageData.entries())
      .map(([timestamp, data]) => ({
        timestamp,
        coverage: {
          statements: data.overall.statements.pct,
          branches: data.overall.branches.pct,
          functions: data.overall.functions.pct,
        },
      }));
  }
}

module.exports = new CoverageTracker();

const winston = require('winston');
const path = require('path');
const chalk = require('chalk');
const config = require('../config');

// Define log format
// eslint-disable-next-line no-unused-vars
const logFormat = winston.format.combine(
  winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
  winston.format.errors({ stack: true }),
  winston.format.splat(),
  winston.format.json(),
);

// Custom formatter for console with enhanced colors using chalk
const consoleFormat = winston.format.combine(
  winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
  winston.format.printf(({
    level, message, timestamp, ...meta
  }) => {
    // Define color scheme for different log levels
    const colorize = (text, lvl) => {
      switch (lvl) {
        case 'error':
          return chalk.bold.red(text);
        case 'warn':
          return chalk.keyword('orange')(text);
        case 'info':
          return chalk.bold.blue(text);
        case 'http':
          return chalk.magenta(text);
        case 'verbose':
          return chalk.cyan(text);
        case 'debug':
          return chalk.green(text);
        case 'silly':
          return chalk.grey(text);
        default:
          return text;
      }
    };

    const colorizedLevel = colorize(level.toUpperCase(), level);
    const colorizedTimestamp = chalk.gray(timestamp);
    const metadata = Object.keys(meta).length
      ? chalk.gray(JSON.stringify(meta, null, 2))
      : '';

    return `${colorizedTimestamp} ${colorizedLevel}: ${message} ${metadata}`;
  }),
);

// Add a new format for machine-readable output
const machineFormat = winston.format.printf(({
  level, message, _timestamp, file, line, column, ...meta
}) => {
  // Default to logger.js if no file is specified
  const sourceFile = file || 'logger.js';
  const sourceLine = line || '1';
  const sourceColumn = column || '1';

  return `${sourceFile}:${sourceLine}:${sourceColumn}: ${level} ${message} ${Object.keys(meta).length ? JSON.stringify(meta) : ''
  }`;
});

// Create the logger
const logger = winston.createLogger({
  level: process.env.LOG_LEVEL || 'info',
  format: winston.format.combine(
    winston.format.timestamp(),
    winston.format.errors({ stack: true }),
    winston.format.splat(),
    winston.format.json(),
  ),
  transports: [
    new winston.transports.File({
      filename: path.join('logs', 'error.log'),
      level: 'error',
    }),
    new winston.transports.File({
      filename: path.join('logs', 'combined.log'),
    }),
  ],
});

if (process.env.NODE_ENV !== 'production') {
  logger.add(new winston.transports.Console({
    format: winston.format.combine(
      winston.format.colorize(),
      winston.format.simple(),
    ),
  }));
}

// Add source location tracking
const addSourceLocation = (file, line, column) => (message, meta = {}) => ({
  ...meta,
  file,
  line,
  column,
  message,
});

// Enhance logging methods with source tracking
const enhanceLogger = (originalLogger) => {
  const enhanced = { ...originalLogger };

  ['error', 'warn', 'info', 'http', 'debug'].forEach((level) => {
    enhanced[level] = (message, meta = {}) => {
      const error = new Error();
      const stack = error.stack.split('\n')[2];
      const match = stack.match(/\((.+):(\d+):(\d+)\)$/);
      if (match) {
        const [, file, line, column] = match;
        const sourceLocation = addSourceLocation(file, line, column);
        return originalLogger[level](message, sourceLocation(message, meta));
      }
      return originalLogger[level](message, meta);
    };
  });

  return enhanced;
};

// Add convenience methods for styled logging
logger.success = (message, meta = {}) => {
  logger.info(` ${message}`, meta);
};

logger.important = (message, meta = {}) => {
  logger.info(` ${message}`, meta);
};

logger.highlight = (message, meta = {}) => {
  logger.info(` ${message}`, meta);
};

logger.section = (title, meta = {}) => {
  logger.info(`\n=== ${title} ===\n`, meta);
};

logger.table = (data, meta = {}) => {
  if (Array.isArray(data)) {
    logger.info(`\n${JSON.stringify(data, null, 2)}`, meta);
  } else {
    logger.info(data, meta);
  }
};

// Export the enhanced logger
module.exports = enhanceLogger(logger);

const os = require('os');
const logger = require('./logger');

class MonitoringUtils {
  static getSystemMetrics() {
    const metrics = {
      timestamp: new Date().toISOString(),
      memory: {
        total: os.totalmem(),
        free: os.freemem(),
        used: os.totalmem() - os.freemem(),
        usage: ((os.totalmem() - os.freemem()) / os.totalmem() * 100).toFixed(2),
      },
      cpu: {
        loadAvg: os.loadavg(),
        cpus: os.cpus().length,
      },
      uptime: os.uptime(),
    };

    // Add process-specific metrics
    metrics.process = {
      memory: process.memoryUsage(),
      uptime: process.uptime(),
      pid: process.pid,
    };

    return metrics;
  }

  static logSystemMetrics(interval = 300000) { // Default: 5 minutes
    const logMetrics = () => {
      const metrics = this.getSystemMetrics();
      logger.info('System Metrics:', metrics);

      // Alert on high memory usage
      if (metrics.memory.usage > 90) {
        logger.warn('High memory usage detected:', metrics.memory);
      }

      // Alert on high CPU load
      if (metrics.cpu.loadAvg[0] > metrics.cpu.cpus * 0.8) {
        logger.warn('High CPU load detected:', metrics.cpu);
      }
    };

    logMetrics(); // Initial log
    return setInterval(logMetrics, interval);
  }

  static getProcessMetrics() {
    const usage = process.memoryUsage();
    return {
      heapTotal: usage.heapTotal,
      heapUsed: usage.heapUsed,
      rss: usage.rss,
      external: usage.external,
      uptime: process.uptime(),
    };
  }

  static async checkHealth() {
    const metrics = this.getSystemMetrics();
    const health = {
      status: 'healthy',
      timestamp: new Date().toISOString(),
      metrics: {
        memory: metrics.memory,
        cpu: metrics.cpu,
      },
    };

    // Check memory usage
    if (metrics.memory.usage > 90) {
      health.status = 'unhealthy';
      health.issues = ['High memory usage'];
    }

    // Check CPU load
    if (metrics.cpu.loadAvg[0] > metrics.cpu.cpus * 0.8) {
      health.status = health.status === 'healthy' ? 'degraded' : 'unhealthy';
      health.issues = health.issues || [];
      health.issues.push('High CPU load');
    }

    return health;
  }
}

module.exports = MonitoringUtils;

/**
